import React, { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const PopulationChart = ({ countryCode }) => {
  const [populationData, setPopulationData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [dataFetched, setDataFetched] = useState(false); // Veri alındı kontrolü

  useEffect(() => {
    // Veri çekme işlemi
    setLoading(true); // Yükleniyor durumunu aktif et
    fetch(`https://countriesnow.space/api/v0.1/countries/population/`)
      .then((response) => response.json())
      .then((data) => {
        const countryData = data.data.find(
          (country) => country.country === countryCode
        );

        if (countryData) {
          const formattedData = countryData.populationCounts.map((item) => ({
            year: item.year,
            population: item.value
          }));
          setPopulationData(formattedData);
          setDataFetched(true); // Verinin başarıyla alındığını belirtiyoruz
        }
        setLoading(false); // Yükleniyor durumunu sonlandır
      })
      .catch((error) => {
        console.error('Error:', error);
        setLoading(false); // Yükleniyor durumunu sonlandır
      });
  }, [countryCode]); // countryCode değiştiğinde tekrar veri çek

  if (loading) {
    return <p>Loading population data...</p>;
  }

  if (!dataFetched) {
    return <p>No population data available.</p>;
  }

  const chartData = {
    labels: populationData.map((item) => item.year),
    datasets: [
      {
        label: 'Population Growth',
        data: populationData.map((item) => item.population),
        fill: false,
        borderColor: 'rgba(75,192,192,1)',
        tension: 0.1
      }
    ]
  };

  // Y eksenini veriye göre otomatik ayarlamak
  const options = {
    scales: {
      y: {
        // Y ekseninin min ve max değerlerini otomatik ayarlıyoruz
        min: Math.min(...populationData.map((item) => item.population)) * 0.95,
        max: Math.max(...populationData.map((item) => item.population)) * 1.05
      }
    }
  };

  return (
    <div>
      <h2>Population Growth Over Time</h2>
      <Line data={chartData} options={options} />
    </div>
  );
};

export default PopulationChart;
