import React, { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';

// ChartJS bileşenlerini kaydediyoruz
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const PopulationChart = ({ countryCode }) => {
  const [populationData, setPopulationData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setLoading(true);
    // API'den veri çekiyoruz
    fetch(`https://countriesnow.space/api/v0.1/countries/population/`)
      .then((response) => response.json())
      .then((data) => {
        const countryData = data.data.find(
          (country) => country.country === countryCode
        );

        if (countryData) {
          const formattedData = countryData.populationCounts.map((item) => ({
            year: item.year,
            population: item.value
          }));
          setPopulationData(formattedData);
        }
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error:', error);
        setLoading(false);
      });
  }, [countryCode]);

  // Veriler yükleniyorsa, yükleniyor mesajı gösteriyoruz
  if (loading) {
    return <p>Loading population data...</p>;
  }

  // Grafik için veriyi formatlıyoruz
  const chartData = {
    labels: populationData.map((item) => item.year),
    datasets: [
      {
        label: 'Population Growth',
        data: populationData.map((item) => item.population),
        fill: false,
        borderColor: 'rgba(75,192,192,1)',
        tension: 0.1
      }
    ]
  };

  // Y eksenini dinamik olarak ayarlıyoruz
  const minPopulation = Math.min(...populationData.map((item) => item.population));
  const maxPopulation = Math.max(...populationData.map((item) => item.population));

  // Y ekseni için stepSize hesaplıyoruz
  const stepSize = Math.ceil((maxPopulation - minPopulation) / 6); // 6 bölüme ayırıyoruz

  // Y ekseninin daha düzgün görünmesi için bir boşluk ekliyoruz
  const extraMargin = Math.ceil(stepSize / 2);

  // Grafik seçenekleri
  const options = {
    scales: {
      y: {
        min: minPopulation - extraMargin,
        max: maxPopulation + extraMargin,
        ticks: {
          stepSize: stepSize,
          callback: function(value) {
            // Avrupa formatında sayıları göstermek için
            return value.toLocaleString('de-DE'); // Avrupa formatı: 40.000.000 gibi
          }
        }
      }
    },
    responsive: true,
    plugins: {
      tooltip: {
        callbacks: {
          // Tooltip'te de sayıları düzgün formatlıyoruz
          label: function(tooltipItem) {
            return tooltipItem.raw.toLocaleString('de-DE');
          }
        }
      }
    }
  };

  return (
    <div>
      <h2>Population Growth Over Time</h2>
      {populationData.length > 0 ? (
        <Line data={chartData} options={options} />
      ) : (
        <p>No population data available.</p>
      )}
    </div>
  );
};

export default PopulationChart;
